INSERT INTO ${tableName} (

type
, defaultNy
, originalName
, uuidName
, ext
, size
, delNy
, pseq
, path
)
values (
#{type}
,#{defaultNy}
,#{originalName}
,#{uuidName}
,#{ext}
,#{size}
,0
,#{pseq}
,#{path}

)

mapper.select:
SELECT
a.oypdSeq , a.oypdCate , a.oypdBrandCd , a.oypdBrand , a.oypdName , a.oypdPrice , a.oypdCountryCd , a.oypdDistributorCd , a.oypdManufacturerCd , a.oypdVolumeWeight , a.oypdIdeal , a.oypdExpirationDate , a.oypdHowtoUse , a.oypdIngredients , a.oypdFunctionalNy , a.oypdCautions , a.oypdCustomerService , a.oypdQaStandard , a.oypdDelNy , b.oyspSalePrice , b.oyspDelNy , b.oyspSaleNy , b.oyspExclusiveNy
FROM oyProduct a LEFT JOIN oySaleProduct b ON b.oypdSeq = a.oypdSeq WHERE 1=1 AND a.oypdSeq = #{oypdSeq}

mapper.update:
UPDATE
oyProduct
SET
oypdCate = #{oypdCate}
, oypdBrandCd
= #{oypdBrandCd}
, oypdName = #{oypdName}
, oypdPrice = #{oypdPrice}
,
oypdCountryCd = #{oypdCountryCd}
, oypdDistributorCd =
#{oypdDistributorCd}
, oypdManufacturerCd = #{oypdManufacturerCd}
,
oypdVolumeWeight = #{oypdVolumeWeight}
, oypdIdeal = #{oypdIdeal}
,
oypdExpirationDate = #{oypdExpirationDate}
, oypdHowtoUse =
#{oypdHowtoUse}
, oypdIngredients = #{oypdIngredients}
,
oypdFunctionalNy = #{oypdFunctionalNy}
, oypdCautions = #{oypdCautions}
, oypdCustomerService = #{oypdCustomerService}
, oypdQaStandard =
#{oypdQaStandard}
, oypdDistributorCd = #{oypdDistributorCd}

WHERE 1=1
AND oypdSeq = #{oypdSeq}

UPDATE
oySaleProduct
SET
oyspSaleNy = #{oyspSaleNy}
,
oyspSalePrice = #{oyspSalePrice}
, oyspSaleNy = #{oyspSaleNy}
,
oyspExclusiveNy = #{oyspExclusiveNy}

WHERE 1=1
AND oypdSeq = #{oypdSeq}

UPDATE
${tableName}
SET
defaultNy = #{defaultNy}
,
uuidName = #{uuidName}
, ext = #{ext}
, size = #{size}
, path = #{path}

WHERE 1=1
AND pseq = #{pseq}
AND type = #{type}
