mapper.select:
SELECT a.oymbSeq , a.oymbName , a.oymbNameEng , a.oymbId , a.oymbNickName , a.oymbPassword , a.oymbDelNy , a.oymbGenderCd , a.oymbSkinTypeCd , a.oymbPersonalColorCd , a.oymbSavedCd , a.oymbInterestsCd , a.oymbEmailConsentNy , a.oymbSmsConsentNy , a.oymbPushConsentNy , b.oyjqQuestionCd , b.oyjqAnswer , b.oyjqDelNy , c.oymnSeq , c.oymnDelNy , c.oynaSeq , c.oymbSeq , d.oymaDefaultNy , d.oymaZipCode , d.oymaAddress1 , d.oymaAddress2 , d.oymaAddress3 , d.oymaDelNy , e.oympSeq , e.oympDefaultNy , e.oympTelecomCd , e.oympNumber , e.oympDelNy , f.oymeSeq , f.oymeDefaultNy , f.oymeEmailAccount , f.oymeEmailDomainCd , f.oymeDelNy
FROM oyMember a LEFT JOIN oymbJoinQna b ON b.oymbSeq = a.oymbSeq LEFT JOIN oymbNationality c ON c.oymbSeq = a.oymbSeq LEFT JOIN oymbAddress d ON d.oymbSeq = a.oymbSeq LEFT JOIN oymbPhone e ON e.oympDefaultNy=1 AND e.oymbSeq = a.oymbSeq LEFT JOIN oymbEmail f ON f.oymeDefaultNy=1 AND f.oymbSeq = a.oymbSeq
WHERE 1=1 AND a.oymbSeq = #{oymbSeq}

SELECT oympSeq , oympDefaultNy , oympTypeCd , oympTelecomCd , oympNumber , oympDelNy
FROM oymbPhone
WHERE 1=1 AND oympDelNy = 0 AND oymbSeq = #{oymbSeq}

SELECT oymeSeq , oymeDefaultNy , oymeTypeCd , oymeEmailDomainCd , oymeEmailAccount , oymeEmailFull
FROM oymbEmail
WHERE 1=1 AND oymeDelNy = 0 AND oymbSeq = #{oymbSeq}

mapper.update:
UPDATE
oyMember
SET
oymbName = #{oymbName}
, oymbNameEng
= #{oymbNameEng}
, oymbId = #{oymbId}
, oymbNickName = #{oymbNickName}
,
oymbPassword = #{oymbPassword}
, modDateTime = #{modDateTime}

WHERE 1=1
AND oymbSeq = #{oymbSeq}

UPDATE
oymbAddress
SET
oymaZipCode = #{oymaZipCode}
,
oymaAddress1 = #{oymaAddress1}
, oymaAddress2 = #{oymaAddress2}
,
oymaAddress3 = #{oymaAddress3}
, modDateTime = #{modDateTime}

WHERE 1=1
AND oymbSeq = #{oymbSeq}

UPDATE
oymbJoinQna
SET
oyjqQuestionCd =
#{oyjqQuestionCd}
, oyjqAnswer = #{oyjqAnswer}
, modDateTime =
#{modDateTime}

WHERE 1=1
AND oymbSeq = #{oymbSeq}

UPDATE
oymbNationality
SET
oynaSeq = #{oynaSeq}
,
modDateTime = #{modDateTime}

WHERE 1=1
AND oymbSeq = #{oymbSeq}

UPDATE
oymbPhone
SET
oympTelecomCd = #{oympTelecomCd}
,
oympNumber = #{oympNumber}
, modDateTime = #{modDateTime}

WHERE 1=1
AND
oymbSeq = #{oymbSeq}
AND oympDefaultNy = #{oympDefaultNy}

UPDATE
oymbEmail
SET
oymeEmailFull = #{oymeEmailFull}
,
oymeEmailAccount = #{oymeEmailAccount}
, oymeEmailDomainCd =
#{oymeEmailDomainCd}
, modDateTime = #{modDateTime}

WHERE 1=1
AND oymbSeq
= #{oymbSeq}
AND oymeDefaultNy = #{oymeDefaultNy}
